mapper.select:
SELECT CE.*, U.user_name
FROM CalendarEvent CE
JOIN Users U ON CE.user_id = U.user_id
WHERE CE.user_id = #{userId}
AND CE.t_calendar_id IS NULL;

SELECT CE.*, D.department_name
FROM CalendarEvent CE
JOIN Department D ON CE.department_id = D.department_id
WHERE CE.user_id = #{userId}
AND CE.calendar_id IS NULL;

mapper.insert:
INSERT INTO CalendarEvent ( c_event_date, c_event_title, c_event_description, user_id, department_id, calendar_id,
color_index_id,
notice_id,
) VALUES ( #{cEventDate}, #{cEventTitle}, #{cEventDescription}, #{userId}, #{departmentId}, #{calendarId},
#{colorIndexId},
#{noticeId},
);

INSERT INTO CalendarEvent ( c_event_date, c_event_title, c_event_description, user_id, department_id, t_calendar_id,
color_index_id,
notice_id,
) VALUES ( #{cEventDate}, #{cEventTitle}, #{cEventDescription}, #{userId}, #{departmentId}, #{tCalendarId},
#{colorIndexId},
#{noticeId},
);
